import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { deleteUser, listUsers } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';


export function UserListScreen (){
    const navigate = useNavigate();
    const  userList = useSelector(state => state.userList);
    const { loading, error, users } = userList;

    const  userDelete = useSelector(state => state.userDelete);
    const { loading: loadingDelete, error: errorDelete, success: successDelete } = userDelete;

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listUsers());
    }, [dispatch, successDelete]);

    const deleteHandler = (user) => {
        if(window.confirm('Are you sure?')){
            dispatch(deleteUser(user._id));
        }
    }
    return(
        <div>
            <h1>Users</h1>
            {loadingDelete && <LoadingBox></LoadingBox>}
            {errorDelete && <MessageBox variant="danger">{errorDelete}</MessageBox>}
            {successDelete && <MessageBox variant="success">User deleted successfully</MessageBox>}
            {
                loading? (<LoadingBox></LoadingBox>)
                :
                error? (<MessageBox variant="danger">{error}</MessageBox>)
                :
                (
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Is Seller</th>
                                <th>Is Admin</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                             {users.map((user) => (
                                 <tr key={user._id}>
                                     <td>{user._id}</td>
                                     <td>{user.name}</td>
                                     <td>{user.email}</td>
                                     <td>{user.isSeller ? 'YES' : 'NO'}</td>
                                     <td>{user.isAdmin ? 'YES' : 'NO'}</td>
                                     <td>
                                         <button type="button" className='small' onClick={() => navigate(`user/${user._id}/edit`)}>Edit</button>
                                         <button type="button" className='small' onClick={() => deleteHandler(user)}>Delete</button>
                                     </td>
                                 </tr>
                             ))}
                        </tbody>
                    </table>
                )
            }
        </div>
    )
}
