import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { detailsProduct } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Rating from '../components/Rating';

export default function ProductScreen(){
    let navigate = useNavigate();
    const dispatch = useDispatch();
    const [qty, setQty] = useState(1);
    const productDetails = useSelector(state => state.productDetails);
    const { loading, error, product } = productDetails;

    const productId = document.location.pathname.split("/")[document.location.pathname.split("/").length - 1];

    const userSignin = useSelector(state => state.userSignin);
    const { userInfo } = userSignin;

    const [rating, setRating] = useState(0);
    const [comment, setComment] = useState('');

    useEffect(() => {
        dispatch(detailsProduct(productId))
    }, [dispatch, productId]);

    const addToCartHandler = () => {
        navigate(`/cart/${productId}?qty=${qty}`);
    }

    const submitHandler = (e) => {
        e.preventDefault();
        if(comment && rating){
            dispatch(createComment(productId, {rating, comment, name: userInfo.name}));
        }else{
            alert('Please enter comment and rating');
        }
    }

    return (
        <div>
        { loading ? (<LoadingBox></LoadingBox>) 
        : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
        ) : (
            <div>
            <Link to="/">Back to Result</Link>
            <div className="row top">
                <div className="col-2">
                    <img className="large" src={product.image} alt={product.name}></img>
                </div>
                <div className="col-1">
                    <ul>
                        <li>
                            <h1>{product.name}</h1>
                        </li>
                        <li>
                            <Rating rating={product.rating} numReviews={product.numReviews}></Rating>
                        </li>
                        <li>
                            Price : ${product.price}
                        </li>
                        <li>
                            Description:
                            <p>{product.description}</p>
                        </li>
                    </ul>
                </div>
                <div className="col-1">
                    <div className="card card-body">
                        <ul>
                            <li>
                                Seller <h2>
                                    <Link to={`/seller/${product.seller._id}`}>
                                        {product.seller.seller.name}
                                    </Link>
                                </h2>
                                <Rating rating={product.seller.seller.rating} numReviews={product.seller.seller.numReviews}></Rating>
                            </li>
                            <li>
                                <div className="row">
                                    <div>Price</div>
                                    <div className="price">${product.price}</div>
                                </div>
                            </li>
                            <li>
                                <div className="row">
                                    <div>Status</div>
                                    <div>{product.countInStock>0 ? (<span className="success">In Stock</span>):
                                    (<span className="danger">Out of Stock</span>)
                                    }</div>
                                </div>
                            </li>
                            {
                                product.countInStock > 0 && (
                                <>
                                    <li>
                                        <div className="row"></div>
                                        <div>Quantity</div>
                                        <select value={qty} onChange={(e) => setQty(e.target.value)}>
                                            {[...Array(product.countInStock).keys()].map( x => (
                                                    <option key={x+1} value={x+1}>{x+1}</option>
                                                )
                                            )}
                                        </select>
                                    </li>
                                    <li>
                                        <button onClick={addToCartHandler} className="primary block">Add to Cart</button>
                                    </li>
                                </>
                                )
                            }
                        </ul>
                    </div>
                </div>
            </div>
            <div>
                <h2 id="reviews">
                    Reviews
                </h2>
                {product.reviews.length === 0 && (<MessageBox>There is no review</MessageBox>)}
                <ul>
                    {product.reviews.map((review) => (
                        <li key={review._id}>
                            <strong>{review.name}</strong>
                            <Rating rating={review.rating} caption=' '></Rating>
                            <p> {review.createdAt.substring(0, 10)} </p>
                            <p>{review.comment}</p>
                        </li>
                    ))}
                    <li>
                        {userInfo ? (
                            <form className='form' onSubmit={submitHandler}>
                                <div>
                                    <h2>Write a customer review</h2>
                                </div>

                                <label htmlFor="rating">Rating</label>
                                <select id="rating" value={rating} onChange={(e) => setRating(e.target.value)}>
                                    <option value=''>Select...</option>
                                    <option value='1'>1-Poor</option>
                                    <option value='2'>2-Fair</option>
                                    <option value='3'>3-Good</option>
                                    <option value='4'>4-Very good</option>
                                    <option value='5'>5-Excelent</option>
                                </select>
                                <div>
                                    <label htmlFor='comment'>Comment</label>
                                    <textarea id='comment' value={comment} onChange={(e) => setComment(e.target.value)}></textarea>
                                </div>
                                <div>
                                    <label></label>
                                    <button type='submit' className='primary'>Submit</button>
                                </div>
                            </form>
                        ): (
                            <MessageBox>Please <Link to='/signin'>Sign in</Link> to write review</MessageBox>
                        )}
                    </li>
                </ul>
            </div>
        </div>
        )} 
     </div>

    )
}
