import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from '../../node_modules/react-router/index';
import { createProduct, deleteProduct, listProducts } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { PRODUCT_CREATE_RESET, PRODUCT_DELETE_RESET } from '../constants/productConstants';

export default function ProductListScreen (){
    const sellerMode = document.location.pathname.split("/")[document.location.pathname.split("/seller").length - 1];;
    const productList = useSelector(state => state.productList);
    const { loading, error, products } = productList;

    const productCreate = useSelector(state => state.productCreate);
    const { loading: loadingCreate, error: errorCreate, success: successCreate, product: createdProduct } = productCreate;

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const productDelete = useSelector(state => state.productDelete);
    const { loading: loadingDelete, error: errorDelete, success: successDelete } =  productDelete;

    useEffect(() => {
        if (successCreate) {
          dispatch({ type: PRODUCT_CREATE_RESET });
          navigate(`/product/${createdProduct._id}/edit`);
        }
        if (successDelete) {
          dispatch({ type: PRODUCT_DELETE_RESET });
        }
        dispatch(listProducts());
      }, [createdProduct, dispatch, navigate, successCreate, successDelete]);

    const deleteHandler = (product) => {
        if(window.confirm('Are you sure to delete?')){
            dispatch(deleteProduct(product._id));
        }
    };

    const createHandler = () => {
        dispatch(createProduct());
    }

    return(
        <div>
            <div className='row'>
            <h1>Products</h1>
            <button type='button' className='primary' onClick={createHandler}>Create Product</button>
            </div>

            {loadingDelete && <LoadingBox></LoadingBox>}
            {errorDelete && <MessageBox variant="danger">{errorDelete}</MessageBox>}

            {loadingCreate && <LoadingBox></LoadingBox>}
            {errorCreate && <MessageBox variant="danger">{errorCreate}</MessageBox>}
            {loading? <LoadingBox></LoadingBox>
            : 
            error? <MessageBox variant="danger">{error}</MessageBox>
            :
            <table className='table'>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>NAME</th>
                        <th>PRICE</th>
                        <th>CATEGORY</th>
                        <th>BRAND</th>
                        <th>ACTIONS</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product._id}>
                            <td>{product._id}</td>
                            <td>{product.name}</td>
                            <td>$ {product.price.toFixed(2)}</td>
                            <td>{product.category}</td>
                            <td>{product.brand}</td>
                            <td>
                                <button
                                type="button"
                                className="small"
                                onClick={() => navigate(`/product/${product._id}/edit`)}
                                >
                                Edit
                                </button>
                                <button
                                type="button"
                                className="small"
                                onClick={() => deleteHandler(product)}
                                >
                                Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        }
        </div>
    )
}
