import { useEffect } from 'react';
import 'react-responsive-carousel/lib/styles/carousel.min.css'
import { Carousel } from 'react-responsive-carousel';
import Product from '../components/Product';
import MessageBox from '../components/MessageBox';
import LoadingBox from '../components/LoadingBox';
import { useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../actions/productActions';
import { listTopSellers } from '../actions/userActions';
import { Link } from '../../node_modules/react-router-dom/index';

 export default function HomeScreen() {

    const dispatch = useDispatch();

    const productList = useSelector( (state) => state.productList);
    const { loading, error, products } = productList;

    const listTopSellers = useSelector( (state) => state.listTopSellers);
    const { loading: loadingSellers, error: errorSellers, users: sellers } = listTopSellers;

    useEffect(()=>{
        dispatch(listProducts({}));
        dispatch(listTopSellers());
    }, [dispatch])
   return (
     <div>
       <h2>Top sellers</h2>
       { loadingSellers ? (<LoadingBox></LoadingBox>) 
        : errorSellers ? (
        <MessageBox variant="danger">{errorSellers}</MessageBox>
        ) : (
          <>
            {sellers.length === 0 && <MessageBox>No seller found</MessageBox>}
            <Carousel showArrows autoPlay showThumbs={false}>
              {sellers.map((seller) => (
                <div key={seller._id}>
                  <Link to={`/seller/${seller._id}`}>
                    <img src={seller.seller.logo} alt={seller.seller.name}></img>
                  </Link>
                </div>
              ))}
            </Carousel>
          </>
        )} 
       <h2>Featured products</h2>
        { loading ? (<LoadingBox></LoadingBox>) 
        : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
        ) : (
        <>{products.length === 0 && <MessageBox>No product found</MessageBox>}
          <div className="row center">
                 {products.map((product) => (
                   <Product key={product._id} product={product}></Product>
                 ))}
          </div>
        </>
        )} 
     </div>
   );
 }
